import React, { useState } from "react";
import RecipeCard from "./RecipeCard";
import Container from "react-bootstrap/Container";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import "./../App.css";
import axios from "axios";

function RecipeGrid() {
  let savedRecipes;
  const token = localStorage.getItem("token");
  axios
    .get("http://localhost:4000/user/recipes", { headers: { token: token } })
    .then((res) => {
      savedRecipes = res;
      console.log("Recipes sent to saved recipes page:");
      console.log(savedRecipes);
    })
    .catch((error) => console.log(error));
  return (
    <Container fluid="md">
      <h1>Saved Recipes</h1>
      <Row md="4">
        <>
          {savedRecipes.map((r) => (
            <Col>
              <RecipeCard
                sourceUrl={r.sourceUrl}
                image={r.image}
                dishType={r.dishTypes[0] && r.dishTypes[0].toUpperCase()}
                title={r.title}
                readyInMinutes={r.readyInMinutes}
                aggregateLikes={r.aggregateLikes}
                analyzedInstructions={
                  r.analyzedInstructions[0] === undefined
                    ? ["No instructions given"]
                    : r.analyzedInstructions[0].steps
                }
                extendedIngredients={r.extendedIngredients}
                id={r.id}
              ></RecipeCard>
            </Col>
          ))}
        </>
      </Row>
    </Container>
  );
}
export default RecipeGrid;
