import React, { useState } from "react";
import RecipeCard from "./RecipeCard";
import Container from "react-bootstrap/Container";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import "./../App.css";
import axios from "axios";

function RecipeGrid() {
  let savedRecipes;
  const token = localStorage.getItem("token");
  axios
    .get("http://localhost:4000/user/recipes", { headers: { token: token } })
    .then((res) => {
      console.log("Recipes sent to saved recipes page:");
      console.log(res);
      savedRecipes = res;
      console.log("These are the recipes");
      console.log(res.recipes);
    })
    .catch((error) => console.log(error));
  if (savedRecipes) {
    return (
      <Container fluid="md">
        <h1>Saved Recipes</h1>
        <Row md="4">
          <>
            {savedRecipes.map((r) => (
              <Col>
                <RecipeCard
                  sourceUrl={r.sourceUrl}
                  image={r.image}
                  dishType={r.dishTypes[0] && r.dishTypes[0].toUpperCase()}
                  title={r.title}
                  readyInMinutes={r.readyInMinutes}
                  aggregateLikes={r.aggregateLikes}
                  analyzedInstructions={
                    r.analyzedInstructions[0] === undefined
                      ? ["No instructions given"]
                      : r.analyzedInstructions[0].steps
                  }
                  extendedIngredients={r.extendedIngredients}
                  id={r.id}
                ></RecipeCard>
              </Col>
            ))}
          </>
        </Row>
      </Container>
    );
  } else {
    return <h1>Saved Recipes</h1>;
  }
}
export default RecipeGrid;
