import "./App.css";
import RecipeGrid from "./Components/RecipeGrid";
import SearchBar from "./Components/SearchBar"; // Import the SearchBar component
import React, { useState } from "react";
import { Login } from "./Components/Login";
import { Register } from "./Components/Register";
import randomRecipes from "./randomRecipes.json"; // Import the recipes data

function App() {
  const [currentForm, setCurrentForm] = useState("login");
  const [searchTerm, setSearchTerm] = useState(""); // Add state for the search term
  const [searchBarVisible, setSearchBarVisible] = useState(false); // Add state for search bar visibility

  const toggleForm = (formName) => {
    setCurrentForm(formName);
  };

  const handleSearch = (term) => {
    setSearchTerm(term);
  };

  const toggleSearchBar = () => {
    setSearchBarVisible(!searchBarVisible);
  };

  const filteredRecipes = randomRecipes.recipes.filter((recipe) =>
    recipe.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="App">
      <header className="App-header">
        <div className="search-toggle-container">
          <button onClick={toggleSearchBar} className="search-toggle">
            Toggle Search
          </button>
        </div>
        {searchBarVisible && <SearchBar onSearch={handleSearch} />} {/* Render the SearchBar based on visibility state */}
        <RecipeGrid recipes={filteredRecipes} />
      </header>
      {currentForm === "login" ? (
        <Login onFormSwitch={toggleForm} />
      ) : (
        <Register onFormSwitch={toggleForm} />
      )}
    </div>
  );
}

export default App;
